.products-page.light {
  --bg-color: #ffffff;
  --text-color: #333333;
  --border-color: #e0e0e0;
  --input-bg: #ffffff;
  --filter-bg: #f8f9fa;
}

.products-page.dark {
  --bg-color: #1a1a1a;
  --text-color: #ffffff;
  --border-color: #333333;
  --input-bg: #2a2a2a;
  --filter-bg: #2a2a2a;
}

.products-page {
  min-height: 100vh;
  background: var(--bg-color);
  color: var(--text-color);
  padding: 2rem 0;
}

.container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
}

.page-header {
  text-align: center;
  margin-bottom: 3rem;
}

.page-header h1 {
  font-size: 3rem;
  font-weight: 800;
  color: var(--text-color);
  margin-bottom: 1rem;
}

.page-header p {
  font-size: 1.2rem;
  color: #666;
  margin: 0;
}

.filters {
  background: var(--filter-bg);
  padding: 2rem;
  border-radius: 12px;
  margin-bottom: 3rem;
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
}

.search-bar {
  display: flex;
  justify-content: center;
}

.search-input {
  width: 100%;
  max-width: 400px;
  padding: 1rem 1.5rem;
  border: 2px solid var(--border-color);
  border-radius: 50px;
  font-size: 1rem;
  background: var(--input-bg);
  color: var(--text-color);
  transition: border-color 0.2s ease;
}

.search-input:focus {
  outline: none;
  border-color: #007bff;
}

.search-input::placeholder {
  color: #999;
}

.category-filters {
  display: flex;
  justify-content: center;
  flex-wrap: wrap;
  gap: 1rem;
}

.category-btn {
  padding: 0.75rem 1.5rem;
  border: 2px solid var(--border-color);
  border-radius: 25px;
  background: var(--input-bg);
  color: var(--text-color);
  font-size: 1rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;
}

.category-btn:hover {
  border-color: #007bff;
  transform: translateY(-1px);
}

.category-btn.active {
  background: #007bff;
  color: white;
  border-color: #007bff;
}

.products-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 2rem;
  margin-bottom: 3rem;
}

.no-products {
  text-align: center;
  padding: 4rem 2rem;
  color: #666;
}

.no-products h3 {
  font-size: 1.5rem;
  margin-bottom: 1rem;
  color: var(--text-color);
}

.no-products p {
  font-size: 1rem;
  margin: 0;
}

/* Responsive Design */
@media (max-width: 768px) {
  .container {
    padding: 0 1rem;
  }
  
  .page-header h1 {
    font-size: 2rem;
  }
  
  .page-header p {
    font-size: 1rem;
  }
  
  .filters {
    padding: 1.5rem;
  }
  
  .category-filters {
    justify-content: flex-start;
  }
  
  .category-btn {
    font-size: 0.9rem;
    padding: 0.5rem 1rem;
  }
  
  .products-grid {
    grid-template-columns: 1fr;
    gap: 1.5rem;
  }
}
